INSERT INTO `administrativos` (`id`, `nombreCompleto`, `puesto`, `descripcion`, `imagen`) VALUES
(2, ' Roberto Fernández Bonillo', 'Director Deportivo', '(Bechí, Castellón, 5 de julio de 1962), conocido futbolísticamente como Roberto, es un exfutbolista español cuya carrera discurrió sobre todo en el Valencia Club de Fútbol y el Fútbol Club Barcelona.<br>\r\nTiene también carnet de entrenador y es el actual secretario técnico del Fútbol Club Barcelona.', 'robert.jpg'),
(1, 'Ernesto Valverde', 'Tecnico', '(Viandar de la Vera, Cáceres, 9 de febrero de 1964), conocido como Txingurri (hormiga en euskera), es un exfutbolista y entrenador español de fútbol. Actualmente dirige al Fútbol Club Barcelona, de la Primera División de España.', 'valverde.png'),
(3, 'Josep Maria Bartomeu Floreta', 'Presidente', '(Barcelona, 6 de febrero de 1963) es el actual presidente del Fútbol Club Barcelona tras ser elegido el 18 de julio de 2015.<br>\n            Luego de la renuncia de Sandro Rosell en enero de 2014 debido a la situación judicial del club, se convirtió en el presidente en funciones del F. C. Barcelona, siendo finalmente elegido por los socios el 18 de julio de 2015 con el 54,63% de los votos.', 'bartomeu.jpg');

