--
-- Estructura de tabla para la tabla `lugares`
--

CREATE TABLE IF NOT EXISTS `lugares` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `nombre` varchar(30) NOT NULL,
  `descripcion` text,
  `imagen` varchar(50) DEFAULT NULL,
  `latitud` varchar(30) NOT NULL,
  `longitud` varchar(30) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=10 ;

--
-- Volcado de datos para la tabla `lugares`
--

INSERT INTO `lugares` (`id`, `nombre`, `descripcion`, `imagen`, `latitud`, `longitud`) VALUES
(1, 'Oficinas', '5522', 'barcelona.png', '41.3796481', '2.119346'),
(2, 'Camp Nou', '(denominado en ocasiones erróneamente como Nou Camp), es el recinto deportivo propiedad del Fútbol Club Barcelona, ubicado en el distrito de Les Corts de la ciudad de Barcelona, España.<br><br>Inaugurado el 24 de septiembre de 1957 y catalogado por la UEFA con la «máxima distinción», es el estadio con mayor capacidad de Europa y el quinto de fútbol del mundo, con un aforo actual de 99 354 espectadores.<br><br>La capacidad del estadio ha variado desde su aforo inicial de 93 053 localidades, a su máximo aforo de 120 000 espectadores, con la construcción con motivo del Mundial de España 1982, de la tercera gradería. Finalmente en 1994, su aforo actual se situó por debajo de los 100 000 espectadores, tras la remodelación acometida en aplicación de la normativa que obligaba a eliminar las localidades sin asiento.', 'campNou.png', '41.380896', '2.12282'),
(3, 'Ciudad Deportiva Joan Gamper', '(en catalán, Ciutat Esportiva Joan Gamper) es un complejo deportivo del Fútbol Club Barcelona, inaugurada el 1 de junio de 2006.1<br><br>Ubicada en la localidad barcelonesa de San Juan Despí y con una superficie inicial de 136.839 m², ampliada en 2013 con otra parcela de 26.811 m²,2 acoge los entrenamientos del primer, segundo equipo y los equipos inferiores del club. Durante los fines de semana, los equipos del fútbol base y fútbol femenino disputan sus partidos así como la actividad de buena parte de otros deportes formativos de las secciones en estas excelentes instalaciones. Consta de nueve campos de fútbol, un campo de entrenamiento de porteros de césped natural y un pabellón polideportivo.', 'balones.png', '41.376436', '2.05177');

